Property.Value:
BEGIN  
    INSERT INTO [dbo].[Company]
           ([Name]
           ,[Email]
           ,[Phone]
		   ,[FileRootFolderPath]
           ,[Status]
           ,[CreatedByContactId]
           ,[CreatedDateUtc]

           )
     VALUES
          (
		   @Name,
           @Email, 
           @Phone,
		   @FileRootFolderPath,
           @Status,
           @CreatedByContactId,
           @CreatedDateUtc

		   )
END

