-- Description:	Save file info of Accepted/rejected images
-- =============================================

CREATE PROCEDURE [dbo].[sp_FileTracking_Insert](
    @CompanyId AS INT,
	@SourceDrive AS NVARCHAR(30),
	@ParentDirectory AS NVARCHAR(250),
	@BucketName AS NVARCHAR(50),
	@ActionDate AS DATETIME,
	@ActionType AS NVARCHAR(20),
	@Attachment AS NVARCHAR(250),
	@Comments AS NVARCHAR(MAX),
	@MarkupImageUrl AS NVARCHAR(500),
    @NoOfFiles as int,
    @SelectedFileNames AS NVARCHAR(MAX),
	@CreatedByContactId AS INT,
	@CreatedDateUtc AS DATETIME,
	@Status AS INT
)
AS

BEGIN BEGIN TRY INSERT INTO [dbo].[FileTrackingDetails] ( [FileTrackingId] ,[FileName] ,[FilePathUrl] ,[FileType] ,[FileSize] ,[FileMarkUp] ) VALUES (@FileTrackingId ,@FileName ,@FilePathUrl ,@FileType ,@FileSize ,@FileMarkUp
)
END TRY BEGIN CATCH END CATCH END

-- =============================================
-- Author:		Shabuj Hossain
-- Create date: 13 Jan 2021
-- Description:	Save file info of Accepted/rejected images
-- =============================================

CREATE PROCEDURE [dbo].[sp_FileTrackingDetails_Insert](
     @FileTrackingId int,
     @FileName nvarchar(200),
     @FilePathUrl nvarchar(max),
     @FileType nvarchar(30),
     @FileSize bigint,
     @FileMarkUp nvarchar(100)
    
)
AS

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here SELECT u.Id UserId, u.ContactId, u.RoleId, r.[Name] RoleName, u.Username, u.ProfileImageUrl, u.Status, u.LastLoginDateUtc, u.UserGuid, con.FirstName, con.LastName, con.Email, con.Phone, com.Name CompanyName, con.CompanyId, com.FileRootFolderPath ClientRootFolderPath FROM [dbo].[User] u INNER JOIN [dbo].[Role] r on r.Id = u.RoleId INNER JOIN [dbo].[Contact] con on con.Id = u.ContactId INNER JOIN [dbo].[Company] com on com.Id = con.CompanyId
END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here SELECT [Id] ,[Name] ,[Email] ,[Phone] ,[Status] ,[CreatedByContactId] ,[CreatedDateUtc] ,[ChangedDateUtc] ,[FileRootFolderPath] FROM [dbo].[Company] WITH(NOLOCK)
END

