CREATE PROCEDURE [dbo].[spContact_Insert](
    @CompanyId AS INT,
	@FirstName AS NVARCHAR(100),
	@LastName AS NVARCHAR(100),
	@Email AS NVARCHAR(100),
	@Phone AS NVARCHAR(100),
	@ContactGuid AS VARCHAR(100)
)
AS

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here SELECT [Id] ,[CompanyId] ,[SourceDrive] ,[ParentDirectory] ,[BucketName] ,[ActionDate] ,[ActionType] ,[Attachment] ,[Comments] ,[CreatedByContactId] ,[CreatedDateUtc] ,[Status] FROM [dbo].[FileTracking] WITH(NOLOCK)
END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here SELECT [Id] ,[FileTrackingId] ,[FileName] ,[FilePathUrl] ,[FileType] ,[FileSize] ,[FileMarkUp] FROM [dbo].[FileTrackingDetails] WITH(NOLOCK)
END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here SELECT IDENT_CURRENT(@tableName) as InsertId
END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here SELECT [Id] ,[Name] ,[ChangedDateUtc] ,[RoleGuid] FROM [dbo].[Role] WITH(NOLOCK)
END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here SELECT [Id] ,[ContactId] ,[RoleId] ,[Username] ,[ProfileImageUrl] ,[PasswordHash] ,[PasswordSalt] ,[RegistrationToken] ,[PasswordResetToken] ,[Status] ,[LastLoginDateUtc] ,[LastLogoutDateUtc] ,[LastPasswordChangeUtc] ,[CreateFromUserIp] ,[CreatedDateUtc] ,[ChangedDateUtc] ,[UserGuid] FROM [dbo].[User]
END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here SELECT [Id] ,[ContactId] ,[RoleId] ,[Username] ,[ProfileImageUrl] ,[PasswordHash] ,[PasswordSalt] ,[RegistrationToken] ,[PasswordResetToken] ,[Status] ,[LastLoginDateUtc] ,[LastLogoutDateUtc] ,[LastPasswordChangeUtc] ,[CreateFromUserIp] ,[CreatedDateUtc] ,[ChangedDateUtc] ,[UserGuid] FROM [dbo].[User] WITH(NOLOCK) WHERE [Username] = @UserName
END

